$(document).ready(function () {




    // #preloader-------------------------------

    $(window).on('load', function () {
        $('.prelolader').fadeOut();
    });

    // #preloader-------------------------------------



    // #send_lang
    setInterval(function () {
        var w = $(window).width();
        if(w>=768){
            $(document).mouseup(function (e){ // событие клика по веб-документу
                var div = $("#send_lang"); // тут указываем ID элемента
                if (!div.is(e.target) && div.has(e.target).length === 0) { // и не по его дочерним элементам
                        $(".drop").fadeOut(); // скрываем его
                } else {
                    $(".drop").fadeIn();
                }
            });
            $("#send_lang a").on('click', function (e) {
                e.preventDefault();
            });
        }
    },250);


    // #send_lang


    // top_menu
    setInterval(function () {
        var  scroll = $(window).scrollTop();
        if (scroll > 50) {
            $("#top_menu").addClass('active');
        } else {
            $("#top_menu").removeClass('active');
        }
    },250)
    // top_menu




    // #toggle_btn

    $("#toggle_btn").on('click', function () {
        if($(this).hasClass('active')){
            $(this).removeClass('active');
            $('.elements_top_menu').fadeOut(100).removeClass('active');
        } else {
            $(this).addClass('active');
            $('.elements_top_menu').fadeIn(100).addClass('active');
        }
    })

    // #toggle_btn


    // scroll


        var w = $(window).width();
        if(w<=480){
            $('.don_btn').on('click', function () {
                $('html, body').animate({scrollTop: $('.about').offset().top - 80}, 500);
            });
            $('#consultation').on('click', function () {
                $('html, body').animate({scrollTop: $('.contact_us').offset().top- 80}, 1000);
            })
            $('#detailed').on('click', function () {
                $('html, body').animate({scrollTop: $('.device').offset().top- 80}, 1000);
            })
        } else {
            $('.don_btn').on('click', function () {
                $('html, body').animate({scrollTop: $('.about').offset().top - 100}, 500);
            });
            $('#consultation').on('click', function () {
                $('html, body').animate({scrollTop: $('.contact_us').offset().top- 100}, 1000);
            })
            $('#detailed').on('click', function () {
                $('html, body').animate({scrollTop: $('.device').offset().top- 100}, 1000);
            })
        }



    // scroll



    // video------------------------------------------------

    var myPlayer = $('#my-video'),  // Наше видео
    aspectRatio = 9 / 16;  // Сечение плеера (коэффициент соотношения сторон)

    // Функция, меняющая соотношение
    function resizeVideoJS() {
    var myPlayerWidth = myPlayer.parent().outerWidth(), // Требуемая ширина
        myPlayerHeight = myPlayerWidth * aspectRatio; // Требуемая высота

    myPlayer.width(myPlayerWidth).height(myPlayerHeight); // Устанавливаем размеры
    }

    // При загрузке видео
    myPlayer.ready(function () {
    resizeVideoJS(); // Инициируем функцию
    });

    // При изменении ширины окна
    $(window).resize(function () {
    resizeVideoJS(); // Инициируем функцию
    });

    // video--------------------------------------------


    // #question
        $('.question .wrap .detailed').on('click', function () {
            if($(this).hasClass('active')){
                $(this).removeClass('active');
                $(this).parent().children(".text").slideToggle();
            } else {
                $(this).addClass('active');
                $(this).parent().children(".text").slideToggle();
            }
        })
    // #question

});
  